"use client";
import { useState } from "react";
import type { Metadata } from "next";
import { Poppins } from "next/font/google";
import "../globals.scss";
import Header from "@/components/web/header/Header";
import Footer from "@/components/web/footer/Footer";
import PinPopup from "@/components/secruity_code/Pinpopup";

const poppins = Poppins({
  subsets: ["latin"],
  weight: "400",
});

// export const metadata: Metadata = {
//   title: { default: "EaseMySailing", template: "%s | EaseMySailing" },
//   description: "Generated by create next app",
// };

export default function RootLayout({
  children,
}: Readonly<{
  children: React.ReactNode;
}>) {
  const [showPopup, setShowPopup] = useState(true); // Control visibility of the popup

  const closeModal = () => {
    setShowPopup(false); // Close the popup
  };
  return (
    <html lang="en">
      <body className={poppins.className}>
        <main className="w-full h-screen flex flex-col">
          {showPopup ? (
            <PinPopup closeModal={closeModal} />
          ) : (
            <>
              <Header />

              {children}
              <Footer />
            </>
          )}
        </main>
      </body>
    </html>
  );
}
